import React ,{useState} from "react";
import { BrowserRouter as Router,Routes,Route } from "react-router-dom";
import Home from "./Home";
import Training from "./Training";
import Header from "./Header";
import Footer from "./Footer";
import Placement from "./Placement";
import AboutUs from "./AboutUs";
import Director from "./Director";
import Tpo from "./Tpo";
import Info from "./Info";
import Login from "./Login";
import Register from "./Register";
import { Link } from "react-router-dom";

function App(){
      const [currentForm,setCurrentForm]=useState('login');
      const [fac,setFac]=useState(false);
      function toggleForm(formName){
        setCurrentForm(formName);
      }
      const [data,setData]=useState([{name:"Saiprem Kakumani" ,mail: "saipremkakumani@gmail.com",pass: "6281237154",fac: true}]);
      function obtainData(props){
        const res=props.fac;
        setFac(res);
        setData(prevData => {
          return [...prevData,props];
        })
      }
      function faculty(props){
        const res=props;
        setFac(res);
      }
      function LoR(){
        if(currentForm == 'login'){
           return <Login onFormSwitch={toggleForm} userData={data} isFac={faculty}/>;
        }else{
           return <Register onFormSwitch={toggleForm} onSubmission={obtainData}/>;
        }
        <Link to="/Home"/>
      }
    return <Router>
    <Header />
    <Routes>
        <Route exact path="/Login" element={<LoR />}/>
        <Route exact path="/" element={<Home isFac={fac}/>}/>
        <Route exact path="/Training" element={<Training />}/>
        <Route exact path="/Placement" element={<Placement />}/>
        <Route exact path="/AboutUs" element={<AboutUs />}/>
        <Route exact path="/message/director" element={<Director />}/>
        <Route exact path="/message/tpo" element={<Tpo />}/>
        <Route exact path="/Info" element={<Info />}/>
    </Routes>
    <Footer />
</Router>
}

export default App;
