import React, { useState } from "react";

function CreateArea(){
    const [flag,setFlag]=useState(false);
    function handleClick(event){
        setFlag(true);
        event.preventDefault();
    }
    return <div>
        <form className={flag && "create-note"}>
          {flag && <input name="title" placeholder="Topic"/>}
          {flag && <textarea name="content" placeholder="Enter Information"/>}
          <button className="add" type="submit" onClick={handleClick}>Add</button>
        </form>
    </div>
}
export default CreateArea;
