import { Modal } from "@mui/material";
import React, { useState } from "react";

function Login(props){
  // const [email,setEmail]=useState("");
  // const [pass,setPass]=useState("");
  const [details,setDetails]=useState({email:"",pass:""});
  const [open,setOpen]=useState(true);
  function handleClick(event){
    event.preventDefault();
  }
  function handleChange(props){
    const [name,value]=props.target;
    setDetails(prevDetails => {
      return {
        ...prevDetails,
        [name]: value
      };
    });
  }
  return (
    <Modal open={open}
    onClose={()=>setOpen(false)}
    aria-labelledby="modal-modal-title"
    aria-describedby="modal-modal-description">
      <div className="form-group">
        <form onSubmit={handleClick}>
          <label for="email" className="form-label">EMAIL</label>
          <input className="form-control" value={details.email} type="email" placeholder="name@gmail.com" name="email" onChange={handleChange}/>
          <label for="password" className="form-label">PASSWORD</label>
          <input name="pass" class="form-control" value={details.pass} type="password" placeholder="**********" onChange={handleChange}/>
          <button type="submit" className="btn btn-primary" style={{ marginLeft: 10,marginTop: 10,marginBottom: 10}}>Log In</button>
        </form>
        <button onClick={() => props.onFormSwitch('Register')} className="btn btn-primary" style={{ marginLeft: 10,marginBottom: 10}}>Don't have an Account? Register Here</button>
      </div>
    </Modal>
  )
}

export default Login;
