import React, { useState } from "react";
import Data from './Data';
import { Modal } from "@mui/material";
import Login from "./Login";
import EmailIcon from '@mui/icons-material/Email';
import KeyIcon from '@mui/icons-material/Key';
import DriveFileRenameOutlineIcon from '@mui/icons-material/DriveFileRenameOutline';

function Register(props){
  const [email,setEmail]=useState("");
  const [pass,setPass]=useState("");
  const [name,setName]=useState("");
  const [open,setOpen]=useState(true);
  const [flag,setFlag]=useState(false);
  const [data,setData]=useState([{email: "saiprenkakumani@gmail.com",pass: "6281237154"}]);
  const [ele,setEle]=useState({email: "",pass: ""});
  function handleClick(event){
    setOpen(false);
    setFlag(true);
    console.log(email);
    console.log(pass);
    setEle({email,pass});
    // console.log(ele);
    setData(prevData => {
      return [...prevData,ele];
    });
    console.log(data);
    props.onSubmission(data);
    props.onFormSwitch('login');
    event.preventDefault();
  }
  function handleInput(props){
    const val=props.target.value;
    setEmail(val);
  }
  function handlePass(props){
    const val=props.target.value;
    setPass(val);
  }
  function handleName(props){
    const val=props.target.value;
    setName(val);
  }
  return (
    <Modal open={open}
    onClose={()=>flag && setOpen(false)}
    aria-labelledby="modal-modal-title"
    aria-describedby="modal-modal-description">
      <div className="form-group" style={{height: 270}}>
        <form onSubmit={handleClick}>
          <label className="form-label">
            <DriveFileRenameOutlineIcon />
          </label>
          <input className="form-control" name="name" placeholder="Full Name" onChange={handleName} value={name}/>
          <label for="email" className="form-label">
            <EmailIcon />
          </label>
          <input name="email" className="form-control" type="email" placeholder="name@gmail.com" onChange={handleInput} value={email}/>
          <label for="password" className="form-label">
            <KeyIcon />
          </label>
          <input name="pass" class="form-control" type="password" placeholder="**********" onChange={handlePass} value={pass} />
          <button type="submit" className="btn btn-primary" style={{ marginLeft: 10,marginTop: 10,marginBottom: 10}}>Register Here</button>
          <button onClick={()=>props.onFormSwitch('login')} className="btn btn-primary" style={{ marginLeft: 10,marginTop: 10,marginBottom: 10}}>Already Have An Account? Login Here</button>
        </form>
      </div>
    </Modal>
  )
}
export default Register;
